import './App.css';
import { frontface } from './datasource/databasefront'
import Card from './components/card'
import React, { useState } from 'react'

function App() {
  const [frontext, setfronttext] = useState( '' )
  const [backtext, setbacktext] = useState( '' )

  function handleChange1(event) {
    setfronttext( event.target.value );
  }
  function handleChange2(event) {
    setbacktext( event.target.value );
  }
  function handleSubmit( event ) {
    event.preventDefault();
    frontface.push( {
      id: frontface.length + 1,
      short_story: frontext,
      short_ans: backtext,
    } )
    localStorage.setItem( frontface[frontface.length - 1].id, JSON.stringify( frontface[frontface.length - 1] ) )
    console.log(JSON.parse(localStorage.getItem(frontface[frontface.length-1])))
    console.log(frontface)
  }

  return (
    <div className="App">
      <h1>Creating a FlashCard is now Easy</h1>
      <div className = "inp-section">
        <input className = "inp" placeholder="Type frontface short text" onChange = {handleChange1} />
        <input className="inp" placeholder="Type backface short text" onChange={handleChange2} />
        <button className="submit" type="submit" onClick={ handleSubmit }>Submit</button>
      </div>
      <div id="card-layout" className="layout">
        {frontface.map( front => (
          <Card key = {front.id} fronttext={front.short_story} backtext = {front.short_ans} />
        ))}
      </div>
    </div>
  );
}

export default App;
