import './card.css'

export default function card( {frontface, backface} ) {
    // let id = props.frontface[0].id
    // let fronttext = props.frontface[0].short_story
    console.log( frontface, backface )
    let id = frontface[0].id
    let fronttext = frontface[0].short_story
    let backtext = backface[0].short_ans
    return (
        <div className="flip-card">
            <div class="flip-card-inner">
                <div className="flip-card-front">
                    {fronttext}
                </div>
                <div className="flip-card-back">
                    {backtext}
                </div>
            </div>
        </div>
    )
}
