import './App.css';
import { frontface } from './datasource/databasefront'
import {backface} from './datasource/databaseback'
import Card from './components/card'
import {useState} from 'react'

function App() {
  const [frontext, setfronttext] = useState( '' )

  function handleChange(event) {
    setfronttext( event.target.value );
  }
  return (
    <div className="App">
      <h1>Creating a FlashCard is now Easy</h1>
      <div className = "inp-section">
        <input className = "inp" placeholder="Type frontface short text" onChange = {handleChange} />
        <input className = "inp" placeholder = "Type backface short text" />
      </div>
      <div class="layout">
        <Card frontface={frontface} backface={backface} />
        <Card frontface={frontface} backface={ backface }/>
      </div>
    </div>
  );
}

export default App;
